function formatFormPadre(jsonPadre) {
	var jsonData = {}
	log.info('Saving form data ...');
	
    var arr = chunkSubstr(JSONUtil.toJSON(jsonPadre).replace("\n", ""), 65000);
	if (arr.length > 30) {
		throw "Muchos datos"
	}

	for (i = 0; i < 30; i++) {
		jsonData['jsonModel_' + (i + 1)] = i < arr.length ? arr[i] : ''
	}
	

    for (var i = 0; i < 30; i++) {
        log.info('antes de grabar jsonModel ********************');
        log.info(arr[i]);
        hAPI.setCardValue(["jsonModel_" + (i + 1)], i < arr.length ? arr[i] : "")
        
        if (i== 0){
            log.info(hAPI.getCardValue("jsonModel_1"));
        }
    }
    
    log.info('Form data saved.');
}

function chunkSubstr(str, size) {
	var numChunks = Math.ceil(str.length() / size);
	var chunks = new Array(numChunks);

	for (i = 0, o = 0; i < numChunks; ++i, o += size) {
		chunks[i] = str.substr(o, size)
	}
	return chunks
}

function verificaMejora() {
    log.info("==============================================");
	log.info("MEJORA_PLAZO");
    log.info("==============================================");
    var model = getJsonModel();
    data = JSON.parse(model);

    for (var i=0; i < data.itemsPrincipal.length; i++){
        if (data.itemsPrincipal[i].mejora_plazo){
            return true
        } 
    }
    return false
};


function validaPlazo(){
    var lret = false;
    var valorCpo = hAPI.getCardValue("deadline_validation");

    if (valorCpo == 'S'){
        lret = true
    }

    return lret
};

function aprobadoComercial(){
    var lret = false;
    var valorCpo = hAPI.getCardValue("commercial_approved");

    if (valorCpo == 'A'){
        lret = true
    }

    return lret
};


function cargaCliente() {
    var lret = false;
    var model = getJsonModel();
    data = JSON.parse(model);

    if (!data.codCli && !data.CUITCli){
        lret = true
    }

    return lret
};

